sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
	var incState=g_form.getValue('state');
	jslog("AY - The value of incState is: "+incState);
	if(incState == 1){
		jslog("LINE 8 EXECUTED");
		g_form.addDecoration('state','icon-alert','attenzione');
		g_form.flash('state','steal',-2);
	}
	var incImpact=g_form.getValue('impact');
	if(incImpact == 1 || incImpact == 2 ){
		g_form.removeOption('state',3);
		g_form.removeOption('state',6);
		g_form.removeOption('state',7);
		g_form.removeOption('state',8);
		g_form.addInfoMessage("State option removed");
	}
	else if(!isLoading && ( incImpact != 1 || incImpact != 2)){
		g_form.clearValue('state');
		g_form.addOption('state',3,'On Hold');
		g_form.addOption('state',6,'Resolved');
		g_form.addOption('state',7,'Closed');
		g_form.addOption('state',8,'Canceled');
		g_form.setValue('state',incState);
	}


}
